mapper.insert:
INSERT INTO BOARD (
    BOARD_NO, BOARD_TYPE, BOARD_TITLE, BOARD_CONTENT, MEMBER_NO, BOARD_DATE,BOARD_READ_COUNT,POST_TYPE
) VALUES (
    BOARD_SEQ.NEXTVAL, 3, #{boardTitle}, #{boardContent}, #{memberNo}, SYSDATE,0 ,#{postType}
)

mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{{boardContent}
WHERE BOARD_NO = #{boardNo} AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, MEMBER_NO, TO_CHAR(BOARD_DATE, 'YYYY-MM-DD') AS BOARD_DATE
FROM BOARD
WHERE BOARD_NO = #{boardNo}
